package application.gui;

import java.io.ByteArrayInputStream;
import java.util.Base64;

import javafx.scene.image.Image;
import javafx.scene.image.ImageView;

public class ButtonIconsRender {
	
	public ImageView ivFolderIcn() {
		String base64folder = "";
		Image i = new Image(new ByteArrayInputStream(Base64.getDecoder().decode(base64folder)));
		ImageView iv = new ImageView();
		iv.setFitWidth(24);
		iv.setPreserveRatio(true);
		iv.setImage(i);
		return iv;
	}
	
	public ImageView ivPauseIcn() {
		String base64Pause = "";
		ImageView iv = new ImageView();
		Image i = new Image(new ByteArrayInputStream(Base64.getDecoder().decode(base64Pause)));
		iv.setFitWidth(24);
		iv.setPreserveRatio(true);
		iv.setImage(i);
		return iv;
	}
	
	public ImageView ivPlayIcn() {
		String base64Play = "";
		ImageView iv = new ImageView();
		Image i = new Image(new ByteArrayInputStream(Base64.getDecoder().decode(base64Play)));
		iv.setFitWidth(24);
		iv.setPreserveRatio(true);
		iv.setImage(i);
		return iv;
	}
}
